rm(list = ls())A <- 1B <- 3C <- 1s1 <- ( - B - sqrt(B ^ 2 - 4 * A * C)) / (2 * A)s2 <- ( - B + sqrt(B ^ 2 - 4 * A * C)) / (2 * A)options(digits = 1)solution <- c(s1, s2)	cat("One digit: ")	show(solution)rounded <- c(-2.6, -0.4)options(digits = 6)solution2 <- c(s1, s2)	cat("Six digits: ")	show(solution2)options(digits = 2)error = (solution2 - rounded) / rounded * 100	cat("Error in %: ")	show(error)